document.addEventListener('DOMContentLoaded', () => {
    const themeToggle = document.getElementById('theme-toggle');
    const themeIconLight = document.getElementById('theme-icon-light');
    const themeIconDark = document.getElementById('theme-icon-dark');
    const htmlEl = document.documentElement;

    // Function to apply theme
    const applyTheme = (theme) => {
        if (theme === 'dark') {
            htmlEl.classList.add('dark');
            themeIconLight.classList.add('hidden');
            themeIconDark.classList.remove('hidden');
        } else {
            htmlEl.classList.remove('dark');
            themeIconLight.classList.remove('hidden');
            themeIconDark.classList.add('hidden');
        }
    };

    // Check for saved theme in localStorage or system preference
    const savedTheme = localStorage.getItem('theme');
    const prefersDark = window.matchMedia('(prefers-color-scheme: dark)').matches;

    if (savedTheme) {
        applyTheme(savedTheme);
    } else if (prefersDark) {
        applyTheme('dark');
    } else {
        applyTheme('light');
    }

    // Event listener for the toggle button
    themeToggle.addEventListener('click', () => {
        const isDark = htmlEl.classList.contains('dark');
        if (isDark) {
            applyTheme('light');
            localStorage.setItem('theme', 'light');
        } else {
            applyTheme('dark');
            localStorage.setItem('theme', 'dark');
        }
    });
});

/* Custom styles for elements that are harder to style with Tailwind */

/* For the countdown timer text to prevent wrapping */
#countdown-timer {
    font-variant-numeric: tabular-nums;
}

/* Custom styles for calendar */
.calendar-day-name {
    @apply text-center font-bold text-sm text-gray-500 dark:text-gray-400 p-2;
}

.calendar-day {
    @apply relative text-center border border-gray-200 dark:border-gray-700 h-20 sm:h-24 transition-colors;
}

.calendar-day.other-month {
    @apply text-gray-400 dark:text-gray-600 bg-gray-50 dark:bg-gray-800;
}

.calendar-day.today {
    @apply bg-blue-100 dark:bg-blue-900 border-primary dark:border-accent font-bold;
}

.day-number {
    @apply absolute top-1 right-1 sm:top-2 sm:right-2 text-xs sm:text-sm;
}

const quotes = [
    {
        text: "The future depends on what you do today.",
        author: "Mahatma Gandhi"
    },
    {
        text: "The only way to do great work is to love what you do.",
        author: "Steve Jobs"
    },
    {
        text: "Believe you can and you're halfway there.",
        author: "Theodore Roosevelt"
    },
    {
        text: "Success is not final, failure is not fatal: it is the courage to continue that counts.",
        author: "Winston Churchill"
    },
    {
