];

window.quotes = quotes;

const uiModule = (() => {
    const DOMElements = {
        countdownTitle: document.getElementById('countdown-title'),
        countdownSubtitle: document.getElementById('countdown-subtitle'),
        minutesSpan: document.getElementById('minutes'),
        secondsSpan: document.getElementById('seconds'),
        progressBar: document.getElementById('progress-bar'),
        todayScheduleContainer: document.getElementById('today-schedule-container'),
        alertContainer: document.getElementById('alert-container'),
        weatherContainer: document.getElementById('weather-container'),
        quoteContainer: document.getElementById('quote-container'),
    };

    const renderCountdown = (title, subtitle, minutes, seconds) => {
        if (DOMElements.countdownTitle) DOMElements.countdownTitle.textContent = title;
        if (DOMElements.countdownSubtitle) DOMElements.countdownSubtitle.textContent = subtitle;
        if (DOMElements.minutesSpan) DOMElements.minutesSpan.textContent = String(minutes).padStart(2, '0');
        if (DOMElements.secondsSpan) DOMElements.secondsSpan.textContent = String(seconds).padStart(2, '0');
    };

    const updateProgressBar = (percentage) => {
        if (DOMElements.progressBar) {
            DOMElements.progressBar.style.width = `${percentage}%`;
        }
    };

    const renderTodaySchedule = (schedule, currentPeriodName) => {
        if (!DOMElements.todayScheduleContainer) return;
        if (!schedule || schedule.periods.length === 0) {
            DOMElements.todayScheduleContainer.innerHTML = `<p class="text-gray-500 dark:text-gray-400">${schedule.name} - No classes today!</p>`;
            return;
        }

        const scheduleHtml = schedule.periods.map(period => {
            const isCurrent = period.name === currentPeriodName;
            const isPassing = period.name.toLowerCase().includes('passing');
            if(isPassing) return ''; // Don't show passing periods in the summary

            return `
                <div class="flex justify-between items-center p-3 rounded-lg ${isCurrent ? 'bg-primary/10 dark:bg-accent/20' : ''}">
                    <div class="flex items-center">
                        ${isCurrent ? '<i class="fas fa-clock fa-spin mr-3 text-primary dark:text-accent"></i>' : '<i class="far fa-clock mr-3 text-gray-400"></i>'}
                        <span class="font-semibold ${isCurrent ? 'text-primary dark:text-accent' : ''}">${period.name}</span>
                    </div>
                    <span class="text-sm text-gray-600 dark:text-gray-300">${period.start} - ${period.end}</span>
                </div>
            `;
        }).join('');

        DOMElements.todayScheduleContainer.innerHTML = scheduleHtml;
    };

    const renderFullSchedule = (schedule, container) => {
        if (!container) return;
        const scheduleHtml = schedule.periods.map(period => {
             const isPassing = period.name.toLowerCase().includes('passing');
             if(isPassing) return ''; // Don't show passing periods in full view
            return `
                 <div class="flex justify-between items-center p-3 rounded-lg hover:bg-gray-100 dark:hover:bg-gray-700">
                    <span class="font-semibold">${period.name}</span>
                    <span class="text-sm text-gray-600 dark:text-gray-300">${period.start} - ${period.end}</span>
                </div>
            `;
        }).join('');
        container.innerHTML = scheduleHtml;
    }

    const renderAlert = (type, message) => {
        if (!DOMElements.alertContainer) return;
        let bgColor, icon;
        switch (type) {
            case 'lockdown':
                bgColor = 'bg-red-600';
                icon = 'fa-lock';
                break;
            case 'maintenance':
                bgColor = 'bg-yellow-500';
                icon = 'fa-exclamation-triangle';
                break;
            default:
                bgColor = 'bg-blue-500';
                icon = 'fa-info-circle';
        }
        DOMElements.alertContainer.innerHTML = `
            <div class="${bgColor} text-white p-4 rounded-lg shadow-lg flex items-center">
                <i class="fas ${icon} text-2xl mr-4"></i>
                <p class="font-semibold">${message}</p>
            </div>
        `;
    };

    const renderWeather = (data) => {
        if (!DOMElements.weatherContainer) return;
        const iconUrl = `https://openweathermap.org/img/wn/${data.weather[0].icon}@2x.png`;
        DOMElements.weatherContainer.innerHTML = `
            <div class="flex items-center justify-between w-full">
                <div>
                    <h3 class="text-lg font-bold">${data.name}, AZ</h3>
                    <p class="text-4xl font-bold">${Math.round(data.main.temp)}¬∞F</p>
                    <p class="capitalize text-gray-500 dark:text-gray-400">${data.weather[0].description}</p>
                </div>
                <img src="${iconUrl}" alt="Weather icon" class="w-20 h-20">
            </div>
        `;
    };

     const renderWeatherError = () => {
        if (!DOMElements.weatherContainer) return;
        DOMElements.weatherContainer.innerHTML = `
            <div class="text-center w-full">
                 <i class="fas fa-exclamation-circle text-red-500 text-2xl"></i>
                 <p class="mt-2 text-sm">Could not load weather data.</p>
                 <p class="text-xs text-gray-500">Check API key.</p>
            </div>
        `;
    };

    const renderQuote = (quote) => {
        if (!DOMElements.quoteContainer) return;
        DOMElements.quoteContainer.innerHTML = `
            <p class="italic">"${quote.text}"</p>
            <p class="text-right mt-2 font-semibold">- ${quote.author}</p>
        `;
    };

    return {
        renderCountdown,
        updateProgressBar,
        renderTodaySchedule,
        renderFullSchedule,
        renderAlert,
        renderWeather,
        renderWeatherError,
        renderQuote
    };
})();

window.uiModule = uiModule;

const weatherModule = (() => {
    // IMPORTANT: Replace "YOUR_API_KEY" with your actual OpenWeatherMap API key.
    const API_KEY = 'YOUR_API_KEY'; 
    const CITY = 'Mesa';
    const STATE_CODE = 'AZ';
    const COUNTRY_CODE = 'US';
    const UNITS = 'imperial'; // for Fahrenheit

    const fetchWeather = async () => {
        if (API_KEY === 'YOUR_API_KEY') {
            console.error("OpenWeatherMap API key is missing. Please add it to js/modules/weather.js");
            window.uiModule.renderWeatherError();
            return;
        }

        const url = `https://api.openweathermap.org/data/2.5/weather?q=${CITY},${STATE_CODE},${COUNTRY_CODE}&appid=${API_KEY}&units=${UNITS}`;

        try {
            const response = await fetch(url);
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }
            const data = await response.json();
            window.uiModule.renderWeather(data);
        } catch (error) {
            console.error("Error fetching weather data:", error);
            window.uiModule.renderWeatherError();
        }
    };

    return {
        init: fetchWeather
    };
})();

window.weatherModule = weatherModule;

const countdownModule = (() => {
    dayjs.extend(window.dayjs_plugin_customParseFormat);
    dayjs.extend(window.dayjs_plugin_duration);

    let countdownInterval = null;

    const getCurrentSchedule = () => {
        const now = dayjs();
        const dayOfWeek = now.day(); // 0=Sun, 1=Mon, ..., 6=Sat

        switch (dayOfWeek) {
            case 1: // Monday
            case 4: // Thursday
                return window.schedules.aDay;
            case 2: // Tuesday
            case 5: // Friday
                return window.schedules.bDay;
            case 3: // Wednesday
                return window.schedules.wednesday;
            default: // Saturday, Sunday
                return window.schedules.weekend;
        }
    };

    const findCurrentState = (schedule) => {
        const now = dayjs();
        let currentState = { type: 'ended', period: null, nextPeriod: schedule.periods[0] };

        if (!schedule || schedule.periods.length === 0) {
            return { type: 'weekend', period: null, nextPeriod: null };
        }

        for (let i = 0; i < schedule.periods.length; i++) {
            const period = schedule.periods[i];
            const start = dayjs(period.start, "h:mm A");
            const end = dayjs(period.end, "h:mm A");

            if (now.isBefore(start)) {
                currentState = { type: 'before_school', period: null, nextPeriod: schedule.periods[0] };
                break;
            }

            if (now.isAfter(start) && now.isBefore(end)) {
                currentState = { type: 'in_progress', period: period, nextPeriod: schedule.periods[i + 1] || null };
                break;
            }
        }

        if(currentState.type === 'ended'){
             const lastPeriodEnd = dayjs(schedule.periods[schedule.periods.length - 1].end, "h:mm A");
             if(now.isAfter(lastPeriodEnd)){
                 currentState = { type: 'after_school', period: null, nextPeriod: null };
             }
        }

        return currentState;
    };

    const update = () => {
        const schedule = getCurrentSchedule();
        const state = findCurrentState(schedule);
        const now = dayjs();

        let title = '', subtitle = '', targetTime = null, totalDuration = 0;

        switch (state.type) {
            case 'in_progress':
                targetTime = dayjs(state.period.end, "h:mm A");
                title = `${state.period.name} ends in:`;
                subtitle = `Next: ${state.nextPeriod ? state.nextPeriod.name : 'School Ends'}`;
                const periodStart = dayjs(state.period.start, "h:mm A");
                totalDuration = targetTime.diff(periodStart, 'second');
                break;
            case 'before_school':
                targetTime = dayjs(state.nextPeriod.start, "h:mm A");
                title = `School starts in:`;
                subtitle = `First period: ${state.nextPeriod.name}`;
                break;
            case 'after_school':
                title = 'School is over for the day!';
                subtitle = 'Enjoy your evening!';
                break;
            case 'weekend':
                title = 'It\'s the weekend!';
                subtitle = 'No classes today.';
                break;
            default: // ended
                 title = 'School is over for the day!';
                 subtitle = 'Enjoy your evening!';
        }

        window.uiModule.renderTodaySchedule(schedule, state.period ? state.period.name : null);

        if (targetTime) {
            const remaining = targetTime.diff(now, 'second');
            if (remaining < 0) {
                // If time is negative, force an update in a second
                setTimeout(update, 1000);
                return;
            }
            const duration = dayjs.duration(remaining, 'seconds');
            const minutes = duration.minutes();
            const seconds = duration.seconds();
            window.uiModule.renderCountdown(title, subtitle, minutes, seconds);

            if (state.type === 'in_progress' && totalDuration > 0) {
                const elapsed = totalDuration - remaining;
                const percentage = (elapsed / totalDuration) * 100;
                window.uiModule.updateProgressBar(percentage);
            } else {
                 window.uiModule.updateProgressBar(0);
            }
        } else {
            window.uiModule.renderCountdown(title, subtitle, 0, 0);
            window.uiModule.updateProgressBar(100);
        }
    };

    const start = () => {
        if (countdownInterval) {
            clearInterval(countdownInterval);
        }
        update(); // initial call
        countdownInterval = setInterval(update, 1000);
    };

    return {
        init: start
    };
})();

window.countdownModule = countdownModule;

const calendarModule = (() => {
    let currentDate = dayjs();

    const DOMElements = {
        monthYear: document.getElementById('month-year'),
        calendarGrid: document.getElementById('calendar-grid'),
        prevMonthBtn: document.getElementById('prev-month'),
        nextMonthBtn: document.getElementById('next-month'),
    };

    const renderCalendar = () => {
        if (!DOMElements.calendarGrid) return;

        DOMElements.monthYear.textContent = currentDate.format('MMMM YYYY');
        DOMElements.calendarGrid.innerHTML = '';

        const today = dayjs();
        const firstDayOfMonth = currentDate.startOf('month').day();
        const daysInMonth = currentDate.daysInMonth();

        // Day names header
        const dayNames = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
        dayNames.forEach(day => {
            const dayNameEl = document.createElement('div');
            dayNameEl.className = 'calendar-day-name';
            dayNameEl.textContent = day;
            DOMElements.calendarGrid.appendChild(dayNameEl);
        });

        // Blank days for the start of the month
        for (let i = 0; i < firstDayOfMonth; i++) {
            const blankDay = document.createElement('div');
            blankDay.className = 'calendar-day other-month';
            DOMElements.calendarGrid.appendChild(blankDay);
        }

        // Actual days of the month
        for (let i = 1; i <= daysInMonth; i++) {
            const dayEl = document.createElement('div');
            dayEl.className = 'calendar-day';

            const dayNumber = document.createElement('span');
            dayNumber.className = 'day-number';
            dayNumber.textContent = i;
            dayEl.appendChild(dayNumber);

            if (currentDate.date(i).isSame(today, 'day')) {
                dayEl.classList.add('today');
            }

            DOMElements.calendarGrid.appendChild(dayEl);
        }
    };

    const setupEventListeners = () => {
        if(DOMElements.prevMonthBtn) {
            DOMElements.prevMonthBtn.addEventListener('click', () => {
                currentDate = currentDate.subtract(1, 'month');
                renderCalendar();
            });
        }
        if(DOMElements.nextMonthBtn) {
            DOMElements.nextMonthBtn.addEventListener('click', () => {
                currentDate = currentDate.add(1, 'month');
                renderCalendar();
            });
        }
    };

    const init = () => {
        renderCalendar();
        setupEventListeners();
    };

    return { init };

})();

window.calendarModule = calendarModule;

document.addEventListener('DOMContentLoaded', () => {
    // --- THEME SWITCHER --- //
    const themeToggleBtns = document.querySelectorAll('#theme-toggle, #theme-toggle-mobile');
    const html = document.documentElement;

    const applyTheme = (theme) => {
        if (theme === 'dark') {
            html.classList.add('dark');
            localStorage.setItem('theme', 'dark');
        } else {
            html.classList.remove('dark');
            localStorage.setItem('theme', 'light');
        }
    };

    // Check for saved theme in localStorage or use system preference
    const savedTheme = localStorage.getItem('theme');
    const prefersDark = window.matchMedia('(prefers-color-scheme: dark)').matches;

    if (savedTheme) {
        applyTheme(savedTheme);
    } else if (prefersDark) {
        applyTheme('dark');
    }

    themeToggleBtns.forEach(btn => {
        btn.addEventListener('click', () => {
            const isDark = html.classList.contains('dark');
            applyTheme(isDark ? 'light' : 'dark');
        });
    });

    // --- MOBILE MENU --- //
    const mobileMenuButton = document.getElementById('mobile-menu-button');
    const mobileMenu = document.getElementById('mobile-menu');
    if(mobileMenuButton && mobileMenu) {
        mobileMenuButton.addEventListener('click', () => {
            mobileMenu.classList.toggle('hidden');
        });
    }

    // --- PAGE-SPECIFIC INITIALIZATIONS --- //
    const page = window.location.pathname.split('/').pop();

    if (page === 'index.html' || page === '') {
        // --- ALERTS --- //
        // Example alerts. In a real app, this might come from an API.
        // window.uiModule.renderAlert('lockdown', 'Lockdown ‚Äì The campus is on Lockdown. Initiate Lockdown procedures and WAIT for the All Clear.');
        // window.uiModule.renderAlert('maintenance', 'Website Down - Scheduled Website Maintenance');

        // --- QUOTE --- //
        const randomQuote = window.quotes[Math.floor(Math.random() * window.quotes.length)];
        window.uiModule.renderQuote(randomQuote);

        // --- INITIALIZE MODULES --- //
        if (window.countdownModule) {
            window.countdownModule.init();
        }
        if (window.weatherModule) {
            window.weatherModule.init();
        }
    }
});

